import React, { Component } from 'react'

class UserInput extends Component {

   
  render() {
    const { changeProps,username}=this.props;
    return (
      <div>
          <input type="textarea" onChange={changeProps} value={username}/>
        
      </div>
    )
  }
}

export default UserInput;
